mapper.select:
    select * 
    from OG_TestReport 
    where 1=1
    
       AND id = #{id}
    
    
       AND project = #{project}
    
    
       AND lotNo = #{lotNo}
    
    
       AND id in (select testReportId from OG_SensorReport where lotNoSerialNo like #{lotNoLike})
    
    
       AND dateTime = #{dateTime}
    
	
		and dateTime >= #{dateTimeFrom}
	
	
		and dateTime <= #{dateTimeTo}
	
    
       AND firstSensorId = #{firstSensorId}
    
    
		ORDER BY ${orderQuery}
