mapper.select:
    select count(*) 
    from OG_TestReport 
    where 1=1
    
       AND id = #{id}
    
    
       AND project = #{project}
    
    
       AND lotNo = #{lotNo}
    
    
       AND id in (select testReportId from OG_SensorReport where lotNoSerialNo like #{lotNoLike})
    
    
       AND dateTime = #{dateTime}
    
	
		and dateTime >= #{dateTimeFrom}
	
	
		and dateTime <= #{dateTimeTo}
	
    
       AND firstSensorId = #{firstSensorId}

    select * 
    from OG_TestReport 
    where 1=1
    
       AND id = #{id}
    
    
       AND project = #{project}
    
    
       AND lotNo = #{lotNo}
    
    
       AND id in (select testReportId from OG_SensorReport where lotNoSerialNo like #{lotNoLike})
    
    
       AND dateTime = #{dateTime}
    
	
		and dateTime >= #{dateTimeFrom}
	
	
		and dateTime <= #{dateTimeTo}
	
    
       AND firstSensorId = #{firstSensorId}
    
    
		ORDER BY ${orderQuery}

    select sensor.*, code.description as decisionCodeDesc 
	from OG_SensorReport sensor
	left outer join 
	OG_CodeTable code
	on sensor.decisionCode = code.code
    where 1=1
    
       AND id = #{id}
    
    
       AND project = #{project}
    
    
       AND testReportId = #{testReportId}
    
    
       AND sensorNo = #{sensorNo}
    
    
       AND lotNo = #{lotNo}
    
    
       AND dateTime = #{dateTime}
    
    
       AND serialNo = #{serialNo}
    
    ORDER BY ID

select baseTbl.decisionCode , decisionCode as decisionCodeDesc ,count(baseTbl.decisionCode) as codeCount from ( select testReportId
,DATENAME(YYYY, dateTime) +'년 '+ DATENAME(MM, datetime) +'월 ' + case when len(datename(dd, datetime)) = 1 then '0' else '' end + DATENAME(DD, datetime) + '일' AS testDate
,DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(ww, dateTime) + '주' AS testDate
,DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(MM, dateTime) + '월' AS testDate
,DATENAME(yyyy, dateTime)+ '년 ' AS testDate
,case when decisionCode= #{fairQualityCode} then '양품' else '불량' end as decisionCode from OG_sensorReport where 1=1
and DATENAME(YYYY, dateTime) +'년 '+ DATENAME(MM, datetime) +'월 '+ case when len(datename(dd, datetime)) = 1 then '0' else '' end + DATENAME(DD, datetime) + '일' = #{selectTestDate}
and DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(ww, dateTime) + '주' = #{selectTestDate}
and DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(MM, dateTime) + '월' = #{selectTestDate}
and DATENAME(yyyy, dateTime)+ '년 ' = #{selectTestDate}
and dateTime >= #{fromDate}
and dateTime <= #{toDate}
)baseTbl group by baseTbl.decisionCode order by baseTbl.decisionCode desc

select baseTbl.decisionCode ,code.description as decisionCodeDesc ,count(baseTbl.decisionCode) as codeCount from ( select testReportId
,DATENAME(YYYY, dateTime) +'년 '+ DATENAME(MM, datetime) +'월 '+ case when len(datename(dd, datetime)) = 1 then '0' else '' end + DATENAME(DD, datetime) + '일' AS testDate
,DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(ww, dateTime) + '주' AS testDate
,DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(MM, dateTime) + '월' AS testDate
,DATENAME(yyyy, dateTime)+ '년 ' AS testDate
,decisionCode from OG_sensorReport where 1=1 and decisionCode != #{fairQualityCode}
and DATENAME(YYYY, dateTime) +'년 '+ DATENAME(MM, datetime) +'월 '+ case when len(datename(dd, datetime)) = 1 then '0' else '' end + DATENAME(DD, datetime) + '일' = #{selectTestDate}
and DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(ww, dateTime) + '주' = #{selectTestDate}
and DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(MM, dateTime) + '월' = #{selectTestDate}
and DATENAME(yyyy, dateTime)+ '년 ' = #{selectTestDate}
and dateTime >= #{fromDate}
and dateTime <= #{toDate}
)baseTbl left outer join OG_CodeTable code on baseTbl.decisionCode = code.code group by baseTbl.decisionCode, code.description

select * 
from OG_FtpHistory 
where 1=1

   AND id = #{id}


   AND folderName = #{folderName}


   AND fileSerial = #{fileSerial}

select * 
from CHECK_ADS 
where 1=1

   AND sensor_bar = #{sensor_bar}


   AND status = #{status}
